mapper.select:
select * from ringo_chatroom where chatroom_num in
#{chatroom_num}
order by modifie_date desc

mapper.delete:
    	delete 
    		ringo_chat_common

		where
			chat_num in 

				#{chat_num}


			user_num in 

				#{user_num}


			chatroom_num in

				#{chatroom_num}

mapper.insert:
insert into ringo_chatroom
	(
		chatroom_num
		, host_num
		, title
		, lang_category
		, gen_category
	)
	values
	(
		ringo_chatroom_seq.nextval
		, #{host_num}
		, #{title}
		, #{lang_category}
		, #{gen_category}
	)

insert into ringo_chatroom_link
	(
		user_num
		, chatroom_num
	)
	values
	(
		#{user_num}
		, #{chatroom_num}
	)

insert into ringo_chat_common
	(
		chat_num
		, user_num
		, chatroom_num
		, message
		, origin_file
		, saved_file
	)
	values
	{
		#{chat_num}
		, #{user_num}
		, #{chatroom_num}
		, #{message}
		, #{origin_file}
		, #{saved_file}
	}
