mapper.insert:
		insert into ringo_member
			(user_num
			, user_id
			, password
			, username
			, first_name
			, last_name
			, gender
			, birth_date
			, email
			, native_lang
			, target_lang
			, introduction)
		values
			(ringo_member_seq.nextval
			, #{user_id}
			, #{password}
			, #{username}
			, #{first_name}
			, #{last_name}
			, #{gender}
			, #{birth_date}
			, #{email}
			, #{native_lang}
			, #{target_lang}
			,
			 
			
            ''
            #{introduction}
          )

mapper.select:
select count(*)
from ringo_member
where user_id = #{user_id}

select *
	from ringo_member
where
	user_id = #{user_id}
	and
	email = #{email}

mapper.update:
update ringo_member
set 
	password = #{password}
where 
	user_id = #{user_id}

mapper.select:
select count(*)
from ringo_taglink_member
where user_id = #{user_id}
	and tag_num = #{tag_num}

select tag_num
from ringo_taglink_member
where user_num = #{user_num}

mapper.delete:
delete from ringo_taglink_member
where user_num = #{user_num}
	and tag_num = #{tag_num}
